from Bio import Align
from Bio.Align import substitution_matrices

seq1 = ""
seq2 = ""

# set the local mode
aligner = Align.PairwiseAligner()
aligner.mode = 'local'
# print the algorithm
print(f"algo:",aligner.algorithm)
# make alignments after defining the substitution matrix
aligner.substitution_matrix = substitution_matrices.load("BLOSUM62")

aligner.query_open_gap_score = -10
aligner.query_extend_gap_score = -0.5
aligner.target_open_gap_score = -10
aligner.target_extend_gap_score = -0.5
alignments = aligner.align(seq1, seq2)

print(aligner)

# get the alignments score
print(f"alignement score :",alignments.score)
# print the first alignment in the alignments object
print(f"first alignement",alignments[0])